import React, { createContext, useContext, useState, useEffect } from 'react';

const AppContext = createContext();

export const useApp = () => useContext(AppContext);

export const AppProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    const saved = localStorage.getItem('arteriaUser');
    return saved ? JSON.parse(saved) : null;
  });

  const [riskScore, setRiskScore] = useState(() => {
    const saved = localStorage.getItem('riskScore');
    return saved ? JSON.parse(saved) : 0;
  });

  const [baselineVoice, setBaselineVoice] = useState(() => {
    const saved = localStorage.getItem('baselineVoice');
    return saved ? JSON.parse(saved) : null;
  });

  const [baselineFace, setBaselineFace] = useState(() => {
    const saved = localStorage.getItem('baselineFace');
    return saved ? JSON.parse(saved) : null;
  });

  const [answers, setAnswers] = useState(() => {
    const saved = localStorage.getItem('questionAnswers');
    return saved ? JSON.parse(saved) : {};
  });

  const [sessionId] = useState(() =>
    crypto?.randomUUID?.() || Math.random().toString(36).slice(2)
  );

  useEffect(() => {
    if (user) localStorage.setItem('arteriaUser', JSON.stringify(user));
  }, [user]);

  useEffect(() => {
    localStorage.setItem('questionAnswers', JSON.stringify(answers));
  }, [answers]);

  useEffect(() => {
    localStorage.setItem('riskScore', JSON.stringify(riskScore));
  }, [riskScore]);

  useEffect(() => {
    if (baselineVoice) localStorage.setItem('baselineVoice', JSON.stringify(baselineVoice));
  }, [baselineVoice]);

  useEffect(() => {
    if (baselineFace) localStorage.setItem('baselineFace', JSON.stringify(baselineFace));
  }, [baselineFace]);

  const getRiskLevel = () => {
    if (riskScore >= 15) return { level: 'ALTO', color: '#dc3545' };
    if (riskScore >= 8) return { level: 'MODERADO', color: '#ffc107' };
    return { level: 'BAIXO', color: '#28a745' };
  };

  const resetData = () => {
    localStorage.clear();
    setUser(null);
    setRiskScore(0);
    setBaselineVoice(null);
    setBaselineFace(null);
  };

  return (
    <AppContext.Provider value={{
      user, setUser,
      riskScore, setRiskScore,
      baselineVoice, setBaselineVoice,
      baselineFace, setBaselineFace,
      sessionId,
      getRiskLevel,
      resetData
    }}>
      {children}
    </AppContext.Provider>
  );
};
